INSERT INTO `poetry` (`id`, `title`, `name`, `source`, `img`, `date`, `rating`, `shares`) VALUES
(1, 'О ящерицах', 'crocodile', 'Она купила крокодила\nПо сносному весьма тарифу\nИ как ни странно - надо ж в рифму -\nВсе по Москве его водила.\n\nСперва по булоШным Арбата,\nПотом к дантисту, к окулисту,\nПотом в театр драм артистов\nА в месяц это - пол зарплаты.\n\nКормила-то сперва ставридкой,\nСеврюгой...(он неприхотливый\nКак кактус, что не ждет полива)...\nИ на икорку были скидки.\n\nКормила-то сперва ставридкой,\nСеврюгой...(он неприхотливый\nКак кактус, что не ждет полива)...\nИ на икорку были скидки.\n', '../img/crocodile.jpg', '2017-07-24 16:59:02', NULL, NULL),
(2, 'Экспромт ЧП', 'impromptu', 'Манифест К.В.А.\r\nСвобода, равенство и братство\r\nЖгут земноводные сердца.\r\nКакое, право, святотатство\r\nХулить святые небеса.\r\n\r\nВедь на обед у великанов\r\nБыть приглашенным - добрый знак!\r\nГаргантюа, гурман гурманов,\r\nНам доказал, что это так!\r\n\r\nКрутые формы нынче в моде:\r\nЛягушка в кляре, все дела...\r\nЯ за свободу дам по морде,\r\nПусть весь Париж сожгу дотла.\r\n\r\nПантагрюэль, недолго есть\r\nТебе и дичь, и ананасы.\r\nНаш лягушачий манифест\r\nЕще услышишь ты не раз и...\r\n\r\nКак град в тарелку упадем,\r\nМы красной краской мажем щеки.\r\nГрядет наш дружный батальон,\r\nНевозмутимый и жестокий.\r\n\r\nОставит косточки гурман\r\nОт нас на вычурной тарелке,\r\nНо на товарной этикетке\r\nНапишут наши имена!\r\n\r\n(По заданию "град из красных лягушек в эпоху Возрождения во Франции").', '../img/impromptu.jpg', '2017-07-24 16:59:17', NULL, NULL),
(3, 'Конверт', 'envelope', 'Мне письмо пришло от тебя когда,\nЯ совсем не знала, как угадать,\nОткрывать его или, может, смять,\nИ не ведать, как текст мне дорог?\n\nМне письмо пришло от тебя когда,\nЯ совсем не знала, как угадать,\nОткрывать его или, может, смять,\nИ не ведать, как текст мне дорог?\n\nЯ была спокойна и так строга,\nИ держала крепко конверт в руках,\nИ молилась по кругу я всем богам,\nКогда шли мы по коридору.\n\nДверь за дверью, и снова за дверью дверь,\nИ в ответ мы слышали: "Не теперь,\nНичего не выйдет ты верь - не верь"\nИ смотрели на нас с укором.\n\n"Свой конверт я не дам открывать с ножа" -\nГолос мой ни капельки не дрожал,\nИ ковер земной был то сед, то ржав\nНа исходе зимы нескором.\n\nЯ мечтала, как буду тебя читать\nПеречитывать, ночью ложась в кровать,\nЧтобы утром наизусть рассказать,\nИ боясь забыть ненароком.\n\n"Свой конверт я не дам открывать с ножа" -\nГолос мой ни капельки не дрожал,\nИ ковер земной был то сед, то ржав\nНа исходе зимы нескором.\n\nТы пришел, и тебя прочитала я,\nСвоих страхов совсем уже не тая\nИ над чашей покорно ждала змея,\nПовстречав тебя на пороге.\n\nМне б тебя разглядеть средь широких спин.\nИ застыл весь мир - есть лишь ты один.\nА уйдешь - я, как мать космонавта - сына,\nОтпущу тебя в путь-дорогу..\n\nТы не бойся, тебе напишу во сне,\nТы не зря шепнул мне, что смерти нет.\nТот, кто, не читав, разорвал конверт,\nТолько тот пусть боится Бога.\n', '../img/envelope.jpg', '2017-07-24 16:59:24', NULL, NULL),
(4, 'Хрупкие', 'fragile', 'Молли мерещится море.\r\nМолли мечтает о меле.\r\nНе начинай на нечетной неделе.\r\nНочью ненужное ноет.\r\n\r\nСалли сердита на счастье.\r\nСахаром сыплет, свистит и смеется.\r\nСиние сопки и солнце нечасто:\r\nВ сизые волосы свет не совьется.\r\n\r\nКелли как-будто красива:\r\nКто-то когда-то кивнул ей, кокетке.\r\nС кальцием каша несладка.\r\nКости хрупкИ как корявая ветка.\r\n\r\nНочью ненужного нет.\r\nКальция с солью коктейль самый сладкий.\r\nмолли, и салли, и келли в порядке\r\nСпят под присмотром сиделки.', '../img/fragile.jpg', '2017-07-24 16:59:31', NULL, NULL),
(5, 'Пока мы с тобой ехали', 'whilewego', 'Это не страшно, мама,\r\nЭто лишь переход.\r\nВремя течет вперед\r\nВ море с материка.\r\n\r\nСлышится здесь любовь\r\nВ каждом малейшем звуке.\r\nВ эти родные руки\r\nБуду стремиться вновь.\r\n\r\nНу а меня зовет\r\nСтраж из высокой башни,\r\nТронулся лед вчерашний,\r\nСолнце привел восход.\r\n\r\nВ завтрашнем добром сне\r\nСразу тебя замечу\r\nИ устремлюсь навстречу.\r\nПомни, что смерти нет!\r\n', '../img/whilewego.jpg', '2017-07-25 20:24:31', NULL, NULL),
(6, 'Слон', 'elephant', 'Твоя бабушка – белый слон,\r\nНа котором зиждется мир.\r\nВсей семьи оплот и заслон,\r\nАртефакт советских квартир.\r\nВяжет рот. Выжидает сон.\r\n\r\nНа дверном косяке пророс\r\nРазноцветным карандашом\r\nВсех детей или внуков рост.\r\nСчастьем детства был полон дом.\r\nЖизнь большая: Роддом. Погост.\r\n\r\n"Разве честно всем умирать?" -\r\nСреди ночи проснется внук.\r\nСядет бабушка на кровать\r\nПрикоснется покоем рук.\r\n"Ты устала, наверно, мать"...\r\n\r\nИ как мячик, как пинг и понг\r\nБьется память ее в такт.\r\nДети дом продадут. В долг\r\nЗаживешь. Ну не надо так.\r\nИ какой в этом был толк...?\r\n\r\n"Разве честно - не умирать?" -\r\nВозмутится в сердцах внук.\r\n"Бог прибрал бы к себе мать"\r\nСкажет сын или дочь вдруг.\r\nВремя не обернуть вспять.\r\n\r\nВ одиночке казенных стен\r\nСтуком спиц отсчитает век,\r\nЗаточенное в тела плен,\r\nНе смыкая усталых век,\r\nПривидение. Человек!\r\n\r\nСтолько брошено в мир семян.\r\nСтолько брошено в мир имен.\r\nПравнук будет ли юн, румян?\r\nТвоя бабушка - великан.\r\nТвоя бабушка - белый слон.\r\n', '../img/elephant.jpg', '2017-07-25 20:14:42', NULL, NULL),
(7, 'Я лежу на дне широкой реки', 'imlying', 'Я лежу на дне широкой реки.\r\nМимо проплывают баржи, грузовики\r\nНа баржах, моторные лодки,\r\nВертикально ко дну бычок, и скелет селедки,\r\nСудомойка Соня: так хотела забыться!\r\nМожет, кто ещё: у меня плохая память на лица...\r\n\r\nИногда везёт и в солнечную погоду\r\nЯ отчётливо вижу синеву небосвода,\r\nВспоминаю тогда синь в глазах моей мамы,\r\nДля проформы откину челку: загар тогда ляжет прямо.\r\n\r\nЯ мечтаю о том, что когда-нибудь царь речной\r\nСгинет где-нибудь или уйдёт на покой,\r\nИ тогда прощай меланхолия, покедова, грусть\r\nСтану лёгкой, вынырну и с ветром умчусь.\r\n', '../img/imlying.jpg', '2017-07-25 20:16:17', NULL, NULL),
(8, 'Яблочко', 'apple', 'Жизнь как яблочко с плодоножкой:\r\nКакое сорвешь, то и съешь.\r\nКожицу снимешь, дольку на ножик,\r\nПолный больших надежд.\r\n\r\nГде я? Какой-то спальный район,\r\nРазвязка на третьем кольце!\r\nЭтот пейзаж –совсем не мое,\r\nЯ б не хотел это видеть в конце.\r\n\r\nУтро. Конвейер. Локомотив\r\nТянет вагоны в зарю,\r\nСтук колес как мой лейтмотив,\r\nС ним я давно уже не говорю.\r\n\r\nЯблочко катится в точности так,\r\nКак сотню лет назад.\r\nТы в авангарде, иди в атаку.\r\nСтрашно? Закрой глаза.\r\n\r\nКаждый солдат ждет свой отбой,\r\nСвоей сирены гудок.\r\nХочешь покоя? Будет покой.\r\nВ урочное время и срок.\r\n', '../img/apple.jpg', '2017-07-25 20:13:26', NULL, NULL),
(9, 'Пятилетнего себя я повстречал', 'fiveyearold', 'Пятилетнего себя я повстречал:\r\nЯ сидел в углу на кухне и молчал.\r\n\r\nДедушку по скорой увезли,\r\nБрата старшего в ментовку загребли.\r\nМать на сутках, на конвеере, на ленте.\r\nБатя - прочерк на известном документе.\r\n\r\nЗнаю тех, кто так мечтал вернуться в детство.\r\n(Я избавлен от подобного контекста).\r\n\r\nБогу надо бы нехило постараться,\r\nЧтобы в детстве захотел и я остаться.\r\n\r\nПодошел к себе я пятилетке,\r\nПротянул себе я фантик от конфетки:\r\nЧто, браток, пойдем давай отсюда:\r\nЗдесь не будет славно, только худо.\r\n\r\nПосмотрел я на себя через сомненья\r\nИ ответил сам себе: Моё почтение,\r\nЧем там лучше, чем там легче, расскажи мне?\r\n\r\nЯ ответил без особого нажима:\r\nДа ничем, дружок особо там не легче:\r\nБрат наш умер: отказала летом печень,\r\nДед и по сей день торчит на даче\r\n(Дачный наркоман он, не иначе).\r\nМать себе нашла второго мужа,\r\nГде-то с ним живет. Видать, не тужит.\r\n\r\nВ целом жизнь рутина и конвеер.\r\nДом-работа-дом и каждый вечер\r\nВижу из окна я звезды ЛЭПа\r\nВсе довольно сносно. (И нелепо).\r\n\r\nПочему тогда зовешь с собой меня ты?\r\nШило с мылом не резон менять-то.\r\n\r\nЯ подумал и ему ответил:\r\nВышло так, что я один на свете.\r\nНу а мы с тобою вроде не чужие:\r\nВместе веселее будет жить нам.\r\n\r\nПятилетний я тут осмотрелся\r\nИ прислушался, видать, к биенью сердца.\r\n\r\nБлагодарен я теперь судьбе,\r\nПоспешая к пятилетнему себе.\r\nВсе по-прежнему и сносно, и нелепо\r\nИ глаза привычно слепят звезды ЛЭПа,\r\nНо поверьте на душе легко, ребята,\r\nЕсли дома ждешь хоть сам себя ты.', '../img/fiveyearold.jpg', '2017-07-25 15:37:50', NULL, NULL),
(10, 'Праздник', 'holiday', 'Ноша своя не тянет,\nТряпки свои не жмут.\nЗдесь обещали пряник,\nТам расчехлили кнут.\nКто на свою дорогу\nВстал только к тридцати,\nДал был бы Господь им ноги\nДа начертил пути.\nСвет от лучины плавный,\nТише, учи букварь\nИ почитай заглавной\nКаждую божью тварь.\nРаны свои не ноют,\nТрещины на руках.\nЖизнью пошел земною:\nНебо, давай, пока.\nВ гору нести напрасно\nПалку о двух концах.\nПей, веселись: на праздник\nСнимут сына с креста.', '../img/holiday.jpg', '2017-07-25 15:41:05', NULL, NULL),
(11, 'Как возвещают о смерти? Странно:', 'deathannouncement', 'Как возвещают о смерти? Странно:\r\n«Мама, сегодня…послушай, мама...\r\nПодруга бабушки, слышишь, мама,\r\nАрина...Андревна? Андрианна?\r\nТа, во дворе в нелепой панаме…\r\nВечером умерла.\r\nТакие дела».\r\n\r\nКак возвещают о смерти дети:\r\n«Папа, мама…» слова вот эти…\r\n«Папа, милый…» слова такие…\r\n«Папа, послушай, папа, ведь мама…»\r\nНет не могу – пусть скажут другие:\r\n«Ваша супруга вчера умерла.\r\nТакие дела».\r\n\r\nКак возвещают о смерти, если…\r\nШлют телеграммой, по смске:\r\n«Умер ваш кто-то, вот вам повестка,\r\nВ условном месте,\r\nПрибудьте с тестем,\r\nСосиски в тесте,\r\nВ фургоне тесно,\r\nКогда\r\nВся родня пришла.\r\nТакие дела».\r\n\r\nНе так страшна смерть, как ее малюют.\r\nТы тут трясешься, она: раз плюнуть.\r\nЩелчок: ритуально в землю засунут,\r\nГде ловкие черви снуют.\r\nПокойтесь с миром тяжелые сумки,\r\nИ в банке ссуды, и тот рассудок,\r\nЧто холил годы и годы покуда,\r\nВся жизнь прошла.\r\nТакие дела.\r\n\r\nИ только представь:\r\nЛежишь неподвижно,\r\nБыл червем книжным,\r\nТеперь сам как книга\r\nТеперь ни рукой\r\nНи извилиной двигать\r\nНе можешь,\r\nА жизнь так течет, как текла.\r\nТакие дела…', '../img/deathannouncement.jpg', '2017-07-25 20:12:27', NULL, NULL),
(12, 'Я люблю', 'ilove', 'Я люблю тебя, моя клетка.\r\nЯ рожу тебя: плакать в жилетку.\r\nПодберу все, что ты обронишь\r\nИ женю. Ты меня похоронишь.\r\n\r\nЯ люблю тебя, мое тельце,\r\nБудет жить нам с тобой хотеться.\r\nМолока, и поспать, и с мамой\r\n(Самой-самой) смеяться в ванной.\r\n\r\nЯ люблю тебя, мое тело,\r\nНе хочу, чтобы ты взрослело.\r\nВедь вчера девчонка на лавке,\r\nКруглопузая, горько плакала.\r\nИ качалась: ой люли, люли!\r\nМеня больше никто не любит.\r\n\r\nЯ люблю тебя...Ненавижу!\r\nЯ на лестничной клетке нижней.\r\nЯ не помню...Нет, все я помню.\r\nКак я только дошла до дома...\r\nНо о том говорить не стану\r\nДаже маме. (Самой. Самой).\r\n\r\nЯ люблю тебя мое тело.\r\nРодила всех, кого хотела.\r\nИ кого хотела, простила.\r\nПобыла самой милой. Милой.\r\n\r\nЯ люблю тебя, мое тело.\r\nЯ хочу, чтобы ты постарело.\r\nЧтобы самым тяжелым артритом\r\nВсе суставы были разбиты.\r\nЧтоб меня подводила память,\r\nЧтобы рот начал шамкать, картавить,\r\nЧтобы ночью вставать раз по двести,\r\nЧтобы это, и то не есть и...\r\nИ очки, и челюсть вставная,\r\nИ клюка, и нога стальная.\r\n\r\nЯ старухой покладистой буду\r\nИ согласна все это терпеть.\r\nЕсли мир свое явит чудо\r\nИ отложит до старости смерть.\r\n\r\nЯ люблю тебя, оболочка,\r\nХоть хрупкА ты, и так непрОчна\r\nТы дождись меня, я недолго:\r\nУ меня билеты на Бога.', '../img/ilove.jpg', '2017-07-25 23:47:22', NULL, NULL),
(13, 'Первый съест', 'firstwilleat', 'Я так играю, как будто последний на этом острове.\r\nСловно корабль на мель напоролся затемно.\r\nНе замечать остальных вокруг так непросто мне.\r\nХоть я глазаст и верю в высшую математику.\r\n\r\nБуду лечить цингу я профилактически:\r\nСъем я лимон на глазах изумленной публики.\r\nЯ новоявленный Кук, хоть и истерический.\r\nЯ Пржевальского лошадь, реланиум впрысни пять кубиков.\r\n\r\nЯ открыватель земель, моряк и редактор атласа.\r\nЯ словно Майкл Джордан по части глобуса.\r\nЯ морской волк, хоть больше все же похож на пса.\r\nЯ обойду галеон на две трети корпуса.\r\n\r\nЯ дивный новый мир начерчу на ватмане\r\nСвяжутся и развяжутся узы с вечностью.\r\nСдвинутся и раздвинутся стены ватные.\r\nЯ не сойду, корабль идет до конечной.', ' ../img/firstwilleat.jpg', '2017-07-25 23:46:57', NULL, NULL),
(14, 'Ссора', 'quarrel', 'Гаджет гаджету не ровня.\r\nМне нельзя с тобой играть\r\nНе ходи ко мне в коровник.\r\nА не то расстрою мать.\r\nМать всегда меня учила: \r\nНадо друга выбирать\r\nТак, чтоб на всю жизнь хватило.\r\nА не только детский сад.\r\nТак что, понимаешь сам...\r\nМед стекает по усам, \r\nИ кафтан поизносился: там рукав, а тут карман.\r\n\r\nТы айфоновской семеркой мне под нос давай не тычь.\r\nВ нос получишь и не хнычь. Меня только передернет вся вот эта злая дичь.\r\nПалку брошу в твой кораблик и сломаю твой кулич.\r\nБудешь плакать, плакать плакать,\r\nБудешь папу с мамой звать:\r\nВот отец, а вот и мать посреди двора. И слякоть.\r\nБрани хватит на абзац.\r\n\r\nМне пора идти домой.\r\nНе ходи сказал за мной.\r\nПапа мой зампредам в банке\r\nОткрывает утром дверь.\r\nЧто, не веришь? Ну не верь\r\nИ ступай обедать к бабке,\r\nКак всегда, так и теперь,\r\nВедь родителям плевать:\r\nЕсть отец один и мать,\r\nТак и то уж хорошо, что сажали на горшок.\r\n\r\nЗавтра я тебя увижу\r\nСнова на моем дворе\r\nСразу ты вставай на лыжи и кати к своей горе.\r\n\r\nНе играй в мои игрушки,\r\nИ не клей мою подружку\r\nИ в тетрадку на контрольной из-за парты не гляди\r\nИ вообще давай иди, радуйся, что не словил\r\nОт меня что было сил.\r\n\r\nТак сказал мне мой товарищ\r\nДвадцать восемь дней назад.\r\nБольше мы не разговари-\r\nВаем. Боже, как я рад!', '../img/quarrel.jpg', '2017-07-25 23:45:56', NULL, NULL),
(15, 'Груня Горлицына раму выставит по весне', 'grunya', 'Груня Горлицына раму выставит по весне,\r\nГруня Горлицына видит суженого во сне.\r\nПодмела все полы и фартук выстирала добела,\r\nА потом прилегла и почти что совсем померла.\r\n\r\nНо глаза открыла в самый последний момент.\r\nСмотрит вверх, а над нею стоит монумент:\r\nСимпатичный малый, хоть и не при коне,\r\nЕй получше стало даже, вроде бы. И вполне\r\n\r\nЧеловеческим голосом сей монумент говорит:\r\n"Ты заканчивай, Груня делать такой страдающий вид,\r\nНа дворе весна, пора жениха выбирать.\r\nДа пирком, да за свадебку, заканчивай помирать".\r\n\r\nГруня репу чешет и молвит ему в ответ:\r\n"Да а как оно это: жить, да видеть весь божий свет?\r\nДа смеяться, да без оглядки на стыд и срам,\r\nДа так громко, чтобы эхо пошло по дворам,\r\n\r\nКак капель по крышам и О землю майский дождь,\r\nДа так громко, чтоб даже в Москве в мавзолее вождь\r\nДробно вздрогнул всеми своими костьми:\r\nНе умею я так, чёрт меня возьми!"\r\n\r\nМонумент в свою очередь котелок\r\nСтал скрести рукою да морщинить лоб\r\n"Не умеешь ты радоваться видать,\r\nНу а что если взять тебя пощекотать?"\r\n\r\nИ давай чесать ей пятки, и бока скрести,\r\nИ считать всем гулким нутром да до десяти!\r\nДа пошли смешинки гулять по её лицу,\r\nЗатряслись даже волосы, что росли у неё в носу.\r\n\r\nПокатилась по деревне весельем смеха капель,\r\nА за нею оттепель, а за нею апрель,\r\nМай прогнулся радугой по пшеничным полям,\r\nИ за нею следом хихикала мать сырая земля,\r\n\r\nИ полез первоцвет посмотреть из-под снега скорей,\r\nКто смеётся ещё заливистей, чем поёт соловей,\r\nСолнце выкатилось, и роща как свет бела,\r\nИ пропели на ветках почки: Грунечка ожила!\r\n\r\nИ пошла на радостях вся деревня плясать\r\nИ косили летом, пока не затупилась коса.\r\nИ с тех пор поперед весны Грунин смех бежит,\r\nКаждый год весна смешинками возвещает жизнь!', '../img/grunya.jpg', '2017-07-25 23:49:01', NULL, NULL),
(16, 'Ты насытился, брат?', 'fedupbro', 'Ты насытился, брат?\r\nТы напился ли крови и пота?\r\nДогорает шаббат,\r\nВ пряном зареве тонет суббота.\r\n\r\nМедный свет маяка\r\nОтражается в радужном глазе:\r\nСон дурной рыбака,\r\nПотонувшего в сладком намазе.\r\n\r\nМы опять на двоих\r\nРазломили последнюю булку.\r\nКружит брошенный стих\r\nПо заброшенному переулку.\r\n\r\nНам до ночи идти:\r\nМы навеки вросли тело в тело.\r\nЯ прошу обожди,\r\nЧтоб закончить все то, что хотелось.\r\n\r\nНикуда не уйду\r\nПозабыт, обесценен, заброшен.\r\nПовторяй же в бреду:\r\nЯ не брошу, не брошу, не брошу.\r\n\r\nРазломили судьбу\r\nМы на равные горькие части.\r\nВетер воет в трубу\r\nПро свое иудейское счастье.', '../img/fedupbro.jpg', '2017-07-25 23:51:11', NULL, NULL),
(17, 'Карантин', 'quarantine', 'Губы сини, брюхо сине,\r\nМы в тифозном карантине,\r\nМы медведи, что на льдине оторвались и уплыли в ледовитый океан.\r\nДоктор, я попал в канкан!\r\n\r\nДоктор доктор, где же доктор?!\r\nЧтоб портянки, что намокли.\r\nПоложил бы нам на лбы.\r\nВсе мы баловни судьбы.\r\nСунем головы мы в окна, знаменуя жуть борьбы.\r\n\r\nСроки пали нам на плечи,\r\nВремя нас совсем не лечит.\r\nПолитической статьей\r\nЗа доносы на полстрочки\r\nНаградили нас досрочно\r\nИ пустили всех в расход:\r\n\r\nВ шахты, в топку, в оборот, камнеломни, лесопилки,  кирпичи таскать в носилках, смертью развлекать конвой.\r\nКто вернется?\r\nКто живой станет слушать эти бредни?\r\nЖизнь, поверь, не шерри-бренди.\r\nПадай в омут с головой.\r\n\r\nЭто очень доктор больно:\r\nБыть без времени безвольным.\r\nДокатился человек.\r\nГрянет двадцать первый век.\r\nВек гуманности и фальши,\r\nЯ боюсь, что будет дальше,\r\nДоктор, вряд ли стоит знать.\r\n\r\nТак лечите, чтобы разом.\r\nДоктор, ум зашел за разум посреди барака...\r\n\r\nСтоп.\r\n\r\nРуку положи на лоб мне б отдельную палату алым заревом обьято утро северных широт ...руку положи на рот моя грустная синичка.\r\n...мнеприснитсямедсестричка ивоенныйтотпарад, гдеярослыйивстрою...\r\n(А на утро я сгорю,\r\nЖара пламенем обьят).', '../img/quarantine.jpg', '2017-07-25 23:55:17', NULL, NULL),
(18, 'Между', 'between', 'Кому-то сваи в новом доме,\r\nКому-то в коме две недели,\r\nКому-то родственники в Коми,\r\nА я рифмую без оглядки.\r\n\r\nКому каникулы в Дубае,\r\nКому леченье в Тель-Авиве,\r\nКому-то комменты засрали,\r\nЯ с рифмами играю в прятки.\r\n\r\nКому-то десять лет за сотню,\r\nКому-то сотню не отдали,\r\n(Те, что поехали в Дубай?),\r\nА я пишу стихи в тетрадку.\r\n\r\nКому луну, и лучше с неба,\r\nКому на небо, а не в подпол,\r\nА кто всю жизнь свою прохлопал,\r\nА мне бы рифму к слову "сладко".\r\n\r\nМы затерялись где-то между\r\nСтроки про "Метрику наденьте"\r\nИ "Запишите время смерти",\r\nХраня в душе своей надежду.', '../img/between.jpg', '2017-07-25 23:56:20', NULL, NULL),
(19, 'Бой 17', 'clash17', 'Потуши огнеперую ярость мою:\r\nПтица Феникс от жажды мести\r\nПолыхает, не в силах идти в строю,\r\nСвой же пепел когтями месит.\r\n\r\nЯ изгнанник, я красного века бой,\r\nЯ стотысячетонный маятник,\r\nЗакрываю я амбразуру собой \r\nХоть и воин, но очень маленький.\r\n\r\nПаровоз дает отправной гудок,\r\nОтвечают заводы гулом.\r\nЧерез раз указательный спустит курок,\r\nЧерез два к подбородку дуло.\r\n\r\nЯ огромное дерево во дворе,\r\nПо которому ходят в рясах,\r\nЯ корнями чувствую как в земле\r\nЗмеи времени точат лясы.\r\n\r\nМаховик качается: раз и два,\r\nРазгонять станут бег трамваи.\r\nУсидит на плечах твоя голова,\r\nЕсли слова ей не давали.\r\n\r\nРазожги костры, пусть расскажет дым,\r\nКак война всех соседей тронула.\r\nПарень вышел из этой войны седым,\r\nЗатянулся чулок капроновый.\r\n\r\nЯ стотысячекрылый ночной обстрел,\r\nЯ наган под подушкой спящий.\r\nЯ историю перекроить посмел\r\nИз былого да в настоящее.\r\n\r\nМысли рублены в крохи, и кровь во мне\r\nМародеров графских развалин.\r\nЯ начдив сферический на коне, \r\nЧто заданье свое провалит.\r\n\r\nЯ рассказ про луг, полный васильков\r\nВ по-мертвецки стылом окопе,\r\nЯ, теперь и тот молодой старпом,\r\nСпасший жизнь мне на Перекопе.\r\n\r\nБудет жизнь мне в горло вгрызаться: На!\r\nПух земли - вот моя награда,\r\nЯ курантов бой, я - моя страна\r\nНу а большего мне не надо.', '../img/clash17.jpg', '2017-07-25 23:59:21', NULL, NULL),
(20, 'Косточка', 'kernel', '"Была б любовь-не жаль ни крохи хлеба!"\r\nПоди, кому другому расскажи!\r\nГолодный город соблюдающий режим,\r\nТебе зачем нужна эта Победа?\r\n"Домой я больше, дочка не приеду\r\nНа день-на два с простреленным виском..."\r\n"Рассказы с фронта бросим на потом.\r\nМой взгляд замерз в лазури хрусткой неба..."\r\n\r\nКонвеер душ в "полуторке" груженой,\r\nМне кто-то маленький давно его предрек,\r\nПоди найди на кухне уголок,\r\nХранящий память хлебных крошек жженых.\r\nМне голод пел про суток протяженность\r\nПод лавкой, в миг затишья - у окна,\r\nИ знает верно лишь Нева одна,\r\nКак бились ломким пульсом наши жены.\r\n\r\nЯ был. Был город в ледяном кольце.\r\nМетель. Ничто. И, может, что-то глубже.\r\nИ ближе и, необязательно, что лучше.\r\nСнежинки льнули к воску на лице.\r\nТогда мы не искали себе цель,\r\nВ ночи нас рвали мороки на части.\r\nТогда, пожалуй, лучше в нашей власти\r\nЗабвенью было сдаться без затей.\r\n\r\nНо что это, бороться вопреки\r\nПокуда искра воробьем дрожит в аорте?!\r\nМой Ленинград, ты вишенка на торте\r\nВо сне, в бреду я наперегонки,\r\nВрага опередив на полруки,\r\nСхвачу тебя, потом надежно спрячу,\r\nЧтоб по весне, спустя столетие на даче\r\nТебя сажали в землю старики.', '../img/kernel.jpg', '2017-07-26 00:00:55', NULL, NULL),
(21, 'Твой Стикс замерз, пришла зима', 'styx', 'Твой Стикс замерз, пришла зима.\r\nТы, как в блокаду, люд по льду\r\nВезешь, Харон, сложив в карман\r\nМонеты. В нынешнем году\r\n\r\nНа переправе, в лимб как в Крым,\r\nМы заживем в очередях.\r\nКто предпоследним был: вторым\r\nОкажется. Как на дрожжах\r\n\r\nРастем, а время канет вспять:\r\nМы ждем, от бремени легки.\r\nМы разучились умирать\r\nИ класть на веки пятаки.\r\n\r\nТуман сгущается, и в нем\r\nИдти не страшно в мире грёз,\r\nСветя прожИтого огнем,\r\nКоторый ты с собой пронес.', '../img/styx.jpg', '2017-07-26 00:06:16', NULL, NULL),
(22, 'Святой Иероним', 'ieronim', 'Я - лев, могучий вершитель судеб, \r\nЛюбитель хищных затей,\r\nУвенчан гривой, познавший суть,\r\nИ с кисточкой на хвосте.\r\nНо чуть оступился, промазал вдруг,\r\nКак тот знаменитый волк.\r\nИ в лапе заноза, в глазах испуг \r\nИ рев в кадыке замолк.\r\n\r\nСижу и страдаю, но мелок враг,\r\nЧтоб с ним потягаться мне. \r\nСижу и страдаю я как дурак\r\nВ своем неудачном дне.\r\n\r\nНо слышу я чётко, припав к земле,\r\nМохнатою головой,\r\nКак шествует Иероним ко мне,\r\nРешительный и живой.\r\nПодмышкой книга, и тысячей слов\r\nНаделит святой меня,\r\nОн знает так много о жизни львов,\r\nВороны, щегла, коня.\r\nЗанозу он выдернет словно меч\r\nИз камня кельтский король,\r\nЯ лапы поставлю ему на плечи, \r\nЯ за него горой.\r\n\r\nМораль понятна и так проста,\r\nЕе и младенец поймет:\r\nКоль скоро душа честна и чиста,\r\nТы не смотри на форму хвоста,\r\nТебя признает и лев, и кот.\r\n\r\nКоль скоро война, печаль и беда,\r\nЯ верой живу храним\r\nК любому мне, в любое всегда\r\nСпешит, да растет его борода,\r\nЧудесный Иероним.', '../img/ieronim.jpg', '2017-07-26 00:07:24', NULL, NULL),
(23, 'Вот птица', 'heresthebird', 'Вот птица: и каждое перышко в ярком хвосте и крылах\r\nГотово в любую минуту вспорхнуть за оливовой ветвью,\r\nВ земле, в сухокомьях\r\nВ жару копошится медведка\r\nИ сладко от лета на равных в тиши Подмосковья.\r\nВот дом: так рассохлись на солнце, на зное, облупленной краской,\r\nЕго деревянные стены, хранят тишину десятилетий,\r\nИ травы чуть слышно хихикают - ветер,\r\nИграет, щекочет их шуткой смешной и дурацкой.\r\nВот я: как бездонное озеро - небо\r\nНад миром сменяет абрис облаков и стада погоняет\r\nГляжу я как слон пучеглазую псину глотает,\r\nЧтоб слиться с китом, превратившись в дракона на склоне.\r\nСижу в самодельной цветочно-желтковой короне, на дачу приехав в разгар синеокого мая.', '../img/heresthebird.jpg', '2017-07-26 00:10:20', NULL, NULL),
(24, 'Мой кот', 'mycat', 'Мой кот, как корабль в порт, заходит на кухню,\r\nОн главный эксперт по готовности суточных щей, прожарке мяса и прочих подобных вещей, он носом едва повел и уже унюхал.\r\nНо кот, с недавних пор, бывает обставлен псом,\r\nОставлен в семнадцатой степени дальше от цели, ведь пес - мишленовский критик в питательной цепи,\r\nОн чует с порога подъезда обед и молотит хвостом.', '../img/mycat.jpg', '2017-07-26 00:20:35', NULL, NULL),
(25, 'Внутренняя эмиграция', 'innerimigration', 'Я не покину свою планету.\r\nДаже страну свою не покину.\r\nНа эмигрантов наложат вето:\r\nВсе поезда проезжают мимо.\r\n\r\nШаг за порог - ты и сам не сможешь\r\nПреодолеть дожди и заносы,\r\nДушу скорей перелей в порожнее,\r\nЛовко строча на себя доносы:\r\n\r\n"Знаете, этот, который в будни\r\nБродит в моей голове бесприютно,\r\nДверь закрывая в квартиру, будет\r\nСтроить на корабле каюту,\r\n\r\nСинее море в окне рисует,\r\nПол покрывает он яхтным лаком,\r\nЧтобы не думать о том, что всуе\r\nОн. Чтоб в ночи не хотелось плакать.\r\n\r\nСинее море в его окошке,\r\nПарус - его простыня из соли.\r\nОн, там внутри, эмигрант немножко,\r\nСлышит причудливый хрип прибоя"...\r\n\r\nНу а потом и за ним приедут,\r\nСкрутят и сломят, и лишь в день судный\r\nСтанет известно, за кем победа.\r\nКапитан последним покинет судно.', '../img/innerimigration.jpg', '2017-07-26 00:21:43', NULL, NULL),
(26, 'О потерянном времени', 'losttime', 'Люди, пережившие девяностые,\r\nНикогда не окажутся сломлены,\r\nБесхребетными слыли, стали бесхвостыми,\r\nИз воды в эволюцию выкатились по склону.\r\n\r\nИнженеры "ВНИИмеймнемозги"\r\nПрорубили портал в страну дураков.\r\nГиды в мире, где не видно ни зги.\r\nТурагентства атомных городов.\r\nВ этом мире такой хороший улов,\r\nСтолько мудрых здесь полетит голов!\r\n\r\nОхраняли останки цветного металла,\r\nНи шагу назад, пока горит реактор.\r\nКак Чонкин, упрямо, (руководство не отзвало)\r\n"Ни слова из песни", велит редактор.\r\nСтоят на страже, не допустят развала,\r\nИх мать с руки ваучерами торговала.\r\n\r\nПарень, в котором Чернобыль фонит,\r\nСпит и видит, как его лечат,\r\nКак младший брат покоряет МИТ,\r\nОн к нему гостем, с баулом клетчатым\r\n\r\nЧелноком прибывает. Как много легло на плечи.\r\nА в детстве мечтал, наверное, стать разведчиком.\r\n\r\nЛюди хоть краем глаза видевшие Содом...\r\nТы скажешь, стоп, это все фуфло:\r\n"Что нам с того, как горел Белый дом,\r\nДо соседа сверху вообще не дошло.\r\nПару-тройку упрячут сейчас в дурдом,\r\nПару-тройку прикончат потом за углом.\r\n\r\nВзять хотя бы 37 й, ленинградский 42 й - \r\nИм в подметки пойдет 91й и 93й,\r\n98й сгодится для междометий".\r\nНо...дружок, ты рот-то пока прикрой.\r\nИзо рта вылетает пчелиный рой.\r\n\r\nКогда от получки и до зарплаты полгода - счастье,\r\nКогда торговок в ларьках и палатках режет на части\r\nВремя,\r\nКогда доктор наук, соседка Настасья\r\nБорисовна снова беременная,\r\nСнова стоит за стенкой в очереди,\r\nПока как в рапиде танки едут по площади.\r\n\r\nВремя, как самый жестокий рэкет\r\nПереломит хребет, все смолчат:\r\nВ стране дураков, конечно же, нет, \r\nЧтоб хоть за что-нибудь отвечать.\r\nВера старая учит нас слабого защищать.\r\nВера новая учит нас всех прощать.\r\n\r\nКто-то едет на стрелку с куклой:\r\nЧто пожнешь, то посеешь, ужель не знаешь?\r\nТы в кроватке мечтаешь о новой кукле,\r\nБрат на стреме стоит в тулупчике заячьем.\r\n\r\nНе покинешь квартиры, пока не заряжен\r\nКрем и заветная шапочка из фольги.\r\nНа оптовом рынке примерь наряды,\r\nНе транжирь валюту, прибереги.\r\n\r\nДоллар скоро заскачет - \r\nТолько б на хлеб хватило.\r\nДевяностые грусть, девяностые сила,\r\nТот, кто их видел, знает, что это значит.', '../img/losttime.jpg', '2017-07-26 00:22:41', NULL, NULL),
(27, 'Зона отчуждения', 'exclusionzone', 'Я обручилась с мистером Хантингтоном\r\nА лучше б с мистером Томпсоном (Хантером)\r\nЯ мечом обрушилась, что замер над троном,\r\nЯ отравилась Жан-Полем Сартром.\r\n\r\nИз всех возможных моих историй,\r\nИз всех возможных моих событий,\r\nОставить хочу я беседы с морем\r\nИ пару-другую случайных соитий.\r\n\r\nБокалы вверх за журналистику Гонзо!\r\nКто, если не ты, полезет в пещеру?\r\nПей до последнего майские грозы,\r\nС чистой душой лети в экзосферу.\r\n\r\nДо тошноты, до страха и ненависти\r\nЕсть еще чувства на этом свете.\r\nПлясок святого Витта прелесть\r\nЕще не раскинула свои сети.', '../img/exclusionzone.jpg', '2017-07-26 00:27:31', NULL, NULL),
(28, 'Сын дьявола', 'tothedevilason', 'Он вжимается в стул, не знающий ничего\r\nОб анализе (психо), и честности сновидений.\r\nВот бы яблоки кто в духовке запек для него...\r\nОн пока еще мал, (но лих), и по горло полон сомнений.\r\n\r\nНяню в детском саду накануне прищучил муж,\r\nТот, гражданский, или какой-то еще там слизень.\r\nНе закроет на тихий час окно - и уже простужен,\r\nИ потом голова сквозняком, на остаток жизни.\r\n\r\nГода три еще необстрелянный Белый дом,\r\nБанок пять тушенки мать принесет с собрания,\r\nКуда мчится мир понимает она с трудом,\r\nСконцентрировав на науке выжить свое внимание.\r\n\r\nКто тебе во дворе там чего сказал,\r\nПоманил ли конфетой или ракетами в подворотню?\r\nДруг-сосед, постарше, всегда мечтал\r\nОтыскать в кустах один доллар (ну или сотню).\r\n\r\nОмбудсмен, (такой же нереальный, как осьминог),\r\nЧто из песни Битлов, (осьминожий садик),\r\nПрилетит, как волшебник и - будь здоров - \r\nСразу всех разгонит: и стрёмных дядек,\r\n\r\nИ холодных женщин с халой-пучком,\r\nВ чьих глазах Амон Гёт бы скрючился, как младенец.\r\nВсех одной гребенкой: вихор не дай Бог торчком...\r\nЖрица белых вафельных полотенец.\r\n\r\nНа прожиточный минимум урони\r\nПару слез - не более, все по ГОСТу.\r\nТы у них смотри: не выживешь - кончишь дни\r\nНа казенном пайке (что хуже) или погосте.\r\n\r\nОн немного странно в небо глядит,\r\nТолько что приземлился в Нью-Йорке боинг.\r\nЕму тридцать три сегодня, он невредим,\r\nПара лучших знают, что он с шести лет покойник.\r\n\r\nЕго доктор усадит в кресло - "Закрой глаза,\r\nНачинай считать-вспоминать в обратную четко.\r\nРасскажи, что тогда, в перестройку не смог сказать,\r\nСодрогайся от этого космического отсчёта".\r\n\r\nОн вжимается в стул - клеенчатый дерматин,\r\nОн сбежал из клеточек девяностых.\r\nУ него подрастает свободный сын,\r\nНа обоях рисующий анимешных монстров.\r\n\r\nВосьмибитная музыка вшита в мозг:\r\nОн машина совершенная для убийства.\r\nСнится чёрных дыр заманчивый пылесос,\r\nОн обещан дьяволу, как Настасья Кински.', '../img/tothedevilason.jpg', '2017-07-26 00:32:00', NULL, NULL),
(29, 'Посвящение одному гурману', 'feaster', 'Буркина-Фасо, как страшный сон.\r\nТам тебе - не гольф среди лугов!\r\nТам гепард готовит марш-бросок,\r\nТы туда уехать не готов.\r\n\r\nНо забыв про устриц хоровод\r\nУтомительной унылой чередой,\r\nТы возьмёшь билет на самолет\r\nИ умчишься к Африке седой.\r\n\r\nТам на озере заждался уж жираф,\r\nИ повстанцы заждались в кустах,\r\nТам гепард заждался среди трав,\r\nБудет ветер виться в волосах.\r\n\r\nНи к чему ветчинные витки\r\nНе нужна тебе вдова Клико,\r\nЧтобы не давили потолки,\r\nНужно лишь уехать далеко!', '../img/feaster.jpg', '2017-07-26 00:40:14', NULL, NULL),
(30, 'Легионер', 'legio', 'Кем бы ты был с другим приятелем?\r\nВ других обстоятельствах,\r\nИли теле?\r\nВремя по кругу идет: все просто.\r\nВот, например, о святых апостолах\r\nЯ рассказать хотел бы.\r\n\r\nДва добрых брата, два рыбака,\r\n(Отца их Ионой звали)\r\nСердцем знали: жизнь нелегка\r\nИ сеть "человеков" поймали.\r\nТретий не верил, четвертый продал,\r\nКаждый был мудр не по годам...\r\n\r\nКаждый был принят, каждый помазан\r\nЖизнью платил за святые рассказы.\r\n\r\nНу так и что же? А ты уверен,\r\nЧто был бы тогда идее их верен,\r\nЧто камень не поднял бы в гневной руке,\r\nИ не танцевал, узрев вдалеке,\r\n\r\nКрест, человека и алые стигмы?\r\nЧто было в сердце твоей парадигмы?\r\nВесь тайный смысл ты понимал?\r\nИли "какой-то безумец изгнал\r\nТорговцев из храма, чудак". Фарисей,\r\nЧему учил ты своих детей?\r\nПалец вверх-вниз поднимать надо рвом,\r\nИ на каникулы ездить в Содом?\r\nТело свое воскрешая в термах,\r\nДумать в тщеславии, что ты первый\r\nИзбранный миром,\r\nМазанный миром,\r\n\r\nАлчно считал кубометры квартиры,\r\nВыплатив долг за две тысячи лет.\r\nБьешь себя пяткою в грудь: нет-нет\r\nЯ тот, кто шел с иерихонской трубой,\r\nА не в страхе сидел за стеной.\r\n\r\nРыбу и хлеб принимая смиренно\r\nВ дар от Христа, не роптал о Вселенной.\r\nВерил, прощал, подставляя щеку,\r\nВторую.\r\n\r\nБыл одиноким.\r\nБыл ли ты лучшим в святом легионе\r\nИли сорвал на тебе погоны\r\nОгненный ангел и плакал над телом,\r\nПока душа к истокам летела?', '../img/legio.jpg', '2017-07-26 00:42:18', NULL, NULL),
(31, 'Прокуратор Иудеи', 'procurator', 'Спит поселок, наутро выпадет снег,\r\nСерым сном изогнулись бараки.\r\nСпи скорей, сколько можно уже, человек,\r\nКулаками махать после драки.\r\n\r\nДремлет пес-старикашка, единственный друг,\r\nОн на лапах печаль убаюкал.\r\nНочь рассыпала месяц и звезды вокруг,\r\nТлеет неба полярного купол.\r\n\r\nПрокуратор не спит в этой стылой ночи:\r\nСправедливой системы каратель.\r\nКак уснуть, если в памяти стертой молчит\r\nБезымянный школьный приятель?\r\n\r\nОн забыл тусклый свет в черной арке двора\r\nИ безвольно упавшие плечи,\r\nДесять лет он не вспомнит теперь до утра:\r\nОт жестокости время нас лечит.\r\n\r\nЗадыхалась чубушником майская ночь,\r\nПес на лапах печаль убаюкал.\r\nПрокуратор навряд ли сумел бы помочь...\r\nВоротился в квартиру. Ни звука\r\n\r\nЗа прикрытыми шторами в спальне. Следил\r\nЗа луной, пожирающей слабых,\r\nЗабывал, как сегодня он руки умыл,\r\nВ приговоре слова накорябав\r\n\r\nДругу детства: смех и в глазах огоньки,\r\nВерной помощи в холод и стужу,\r\nДругу, вечно делившему хлеб вопреки.\r\nТому другу, что помнил про дружбу.\r\n\r\nВидит Бог, что никто ему не судья,\r\nПросто сказано было: "так надо"\r\nЗа парад в сорок пятом и залп октября,\r\nЗа солдат, что вернулись с парадом.\r\n\r\nПросто руку занёс и подписан приказ:\r\nТо не я, то решение свыше.\r\nОн привык, он проделывал это не раз,\r\nНа погост отправляя мальчишек.\r\n\r\nЗаметает поселок январский буран,\r\nКак чубушником май заметало.\r\nПрокуратора сломит граненый стакан,\r\nКак сломило свинцовым металлом\r\n\r\nДруга детства: смех и в глазах огоньки,\r\nПомощь верную в холод и стужу,\r\nХлеб последний делившего с ним вопреки,\r\nДо последнего веря в их дружбу.\r\n\r\nДесять лет. Наша память стирает с листа,\r\nВсе, что ночью приснится вопросом.\r\nТак уйдет человек, и не вспомнишь лица,\r\nЗагасив в темноте папиросу.', '../img/procurator.jpg', '2017-07-26 00:43:41', NULL, NULL),
(32, 'Простите мой французский', 'forgivemyfrench', 'День весенний, прохладней гораздо.\r\nПрежде здесь не бывал я ни разу.\r\nПахнет багетом, корицей, духами.\r\nЯ позвонил из Парижа бы маме.\r\n\r\nЗдравствуй, мама, как слышишь меня?\r\nКак ты и брат, и как вся родня?\r\nМаму не слышно ... грохот снарядов:\r\n"Ешь... надевай... береги... так рада".\r\n\r\nЯ бы ответил, bisoux, au revoir\r\nЯ бы пошел гулять на бульвар.\r\n\r\nДолго смотрел как ветер на площади\r\nВ небе их триколор полощет.\r\n\r\nМолился бы утром, читал Коран -\r\nМне подарил его брат, Икрам.\r\nДумал бы: "Вот же какое счастье\r\nСтать целым и больше не рваться на части".\r\n\r\nНе надо дынь и гурий не надо...\r\n\r\nНикто не выйдет из нашего ада!\r\nЖизнь не прочувствую даже на треть.\r\nУвидеть Париж и так умереть:\r\n\r\nЯ не глобальное потепление,\r\nЯ не стран ЕС разобщение,\r\nЯ не голод, война или мор,\r\nЯ не захватчик вселенной, не вор,\r\nНе банковский кризис, не гибель морали,\r\nНе лозунг, что с баррикад проорали,\r\nНе падшая женщина с проклятой печенью,\r\nНе грешниксиндромом слабости меченный,\r\nНе шведский инцест, и не эватаназия,\r\nНе ганжа Голландии, не НАТО экспансия,\r\nНе руки солдат под юбкой у Сирии,\r\nНе прочая форма святого насилия.\r\n\r\nЯ - мальчик - худой, больной и сутулый,\r\nЯ - нервные пальцы, я - острые скулы.\r\n\r\nБез имени мальчик, без племени, дома,\r\nКакой-то идеей безумной ведомый.\r\nЯ сердце готов был утыкать спицами,\r\nЧтоб только стать цельным и не развалиться.\r\n\r\nИ в мой рюкзак бесполезно смотреть,\r\nСмотри мне в глаза - это я, твоя смерть!\r\n\r\nБудь ты Руссо, Жан-Жак или Пьер\r\nМы à la guerre, так будь comme à la guerre!\r\n\r\nЧто ж ты никак не запомнишь, зараза,\r\nХумус тебе не синоним Хамаса,\r\nЖертвы ИГИЛ не вернуть из могил,\r\nЕсли собою я их погубил.\r\n\r\nВ долю секунды получишь ответ:\r\nЯ докажу - твоего Бога нет,\r\nИ райского сада. Моли - не моли.\r\nЯ был до последнего целым внутри.\r\nЯ Мухаммат, #янеШарли.\r\n', '../imgforgivemyfrench.jpg', '2017-07-26 00:46:09', NULL, NULL),
(33, 'Радио', 'radio', 'Всех накормить я не обещал,\r\nРавно как гибнуть и всех прощать,\r\nТак отчего припали так жадно\r\nК этим словам моим? (нет, мне не жалко).\r\n\r\n"Ты как источник святой, и бьющий", - \r\nКаждый сказал мне: "нам стало лучше.\r\nВ каждом из слов так много любви,\r\nТак напои же нас, напои!\r\n\r\nДай тем словам стечь прямо по горлу,\r\nПроклокотать по артериям гордым,\r\nСловно потоком сердце наполнить,\r\nПомнить о прошлом, о будущем помнить.\r\n\r\nБольше! Пиши! Еще! Пусть больно,\r\nМы никогда не скажем "довольно".\r\nВытрясем душу, слова, и выжмем\r\nТексты до капли. Поди-ка, ближе.\r\n\r\nМы тебя любим, в тебе есть тайна.\r\nТекстами, как коноплёй, начитай нас.\r\nТолько пиши, ведь худо не будет, \r\nЧтобы мы помнить могли: мы - люди".\r\n\r\nСловно у Бредбери с Марса мальчик\r\nВсем угодить хотел, мама плачет:\r\nНе узнает ни лицо, ни ножки,\r\nИ печень, должно быть, чужая, и почки.\r\n\r\nТак же и я: и нашим, и вашим.\r\nМир монохромный в цвета перекрашен.\r\nСловно в раскраске для взрослых... страшно.\r\n\r\nВы же судить не спешите строго,\r\nЯ лишь приемник. Собрал ненароком,\r\nТак слушайте радио: "Голос пророка".', '../img/radio.jpg', '2017-07-26 00:47:00', NULL, NULL),
(34, 'Пацифистам', 'topacifists', '\r\nПацифизм - как стиль жизни - утопия, посмотри:\r\nЧто-то вечно подточит тебя изнутри,\r\nЯ терплю до черты, до сжатых ладоней.\r\nЯ терплю до боли своей, не более.\r\n\r\nПацифизм - во всех смыслах - не больше, чем повод\r\nПротянуть по экватору медный провод.\r\nЗемли новые в жадную горсть собрать бы,\r\nГоревать по погибшим в колониях братьям.\r\n\r\nМы спасем тебя, нам ли не знать, как лучше:\r\nМы тебя христианской любовью задушим.\r\nВырвем золото глаз для слоновьей трости,\r\nПоперек континента метая кости.\r\n\r\nПацифизм искажает мира пропорции.\r\nТак давай, расскажи это мне, миротворцу.\r\nЯ солдат, я покину страну, квартиру,\r\nЯ пойду войной под знаменем Мира.\r\n\r\nПацифизм - это залп, не больше, не меньше,\r\nЭто кровь на руках детей или женщин.\r\nНа стене слова: "Прощены Им убийцы\r\nБольше с ними зла уже не случится".', '../img/topacifists.jpg', '2017-07-26 00:48:30', NULL, NULL),
(35, 'Огня', 'fire', 'Что-то со скромностью не заладилось.\r\nМне не скуют доспех из палладия:\r\nМне не мерещатся\r\nАнгелов исповеди,\r\nГрадом не льются в уши.\r\nТот, что бредет в ночи\r\nНи тело мое не хочет, ни душу,\r\nДаже о сердце моем молчит.\r\n\r\nРазве не любо, змей, не просто ли:\r\nС интеллектуального роста\r\nК низостям.\r\nС высоты понтов,\r\nС понтона\r\nГрадом попадать в воду.\r\nТот, что в морской пучине\r\nТоже не хочет близости.\r\nИ о костях молчит.\r\n\r\nРазве не просто, зверь, разве не лакомо:\r\nСтолько раз написала - плакала:\r\n"Шерстьсот шерстдесят шерсть",\r\nИмя твое.\r\nВсе мало тебе.\r\nКак на бумажке лакмусовой\r\nВыведено: "можешь съесть".\r\n\r\nЧто-то со скромностью, как с судьбой:\r\nСтала подружкой ворона.\r\nМне не мерещатся\r\nБольше\r\nлюди.\r\nБольше голов на блюде!\r\nПотчуй меня рассказами, потчуй же!\r\nВ здании трещина.\r\nТы на четыре стороны\r\nСтупай, от меня, не-\r\nмой.\r\n\r\nТам на горе (к заутрене\r\nВетры смеялись, звали нас прочь\r\nОт заката дня)\r\nТягой к Луне запутывал,\r\nПутался сам\r\nи дочерью\r\nТы называл меня.\r\nТот, что в открытом космосе,\r\nРаз заглянул бы в душу\r\nИ дал немного огня.', '../img/fire.jpg', '2017-07-26 00:49:29', NULL, NULL),
(36, 'Летиада', 'summeriad', 'Смешалось: и крепкие клевера стебли\r\nВонзаются в тело, как сабли.\r\nА где-то\r\nНа луг свежескошенный\r\nТравы и стрелы\r\nВсе в ряд:\r\nТак солдаты\r\nЛожатся в окопы.\r\nТо лето прошло.\r\nО проигранной битве\r\nУзнаем по ржаво-\r\nзасохшему лугу\r\nи запаху листьев,\r\nвнезапно опавших.\r\nТы думал, что лето - лихой полководец,\r\nБесстрашно ведущий,\r\nСмертельно разящий\r\nВрагов,\r\nНу а вышло, что лето - как дева:\r\nНа брачное ложе толкнул -\r\nи упала,\r\nБезвольно ослабив звериную хватку,\r\nБессильно свои расставляя колени.\r\nА утром родня,\r\nПростыней потрясая,\r\nБагряные листья в блокноте засушит,\r\nЧтоб вспомнить однажды под вой мерзлой вьюги,\r\nКак солнце над озером томно зависло\r\nВ последний день лета\r\nКак долго смеркалось...', '../img/summeriad.jpg', '2017-07-26 00:51:09', NULL, NULL),
(37, 'Розово солнце', 'pinksun', 'Розово солнце на нервах пылает,\r\nВселяя весну в запотевший рассудок.\r\nНа кухне из крана вода убегает,\r\nСтруится, как кровь по тонким сосудам.\r\nИ ветер простужено что-то бормочет,\r\nВ трубе, в переулках играет дико,\r\n\r\nА грузный старик на базарной площади\r\nСвоей ненаглядной украл гвоздику.\r\nЕго старуха, некогда тонкая,\r\nВетра перемен так тонко чувствует!\r\nНаверное, лучше чувствует только\r\nКорабль в порту слова напутствия.\r\n\r\nСтаруха волнуется: щебет синицы\r\nНа розовой кромке света и ночи.\r\nСтаруха выходит во двор насладиться\r\nТем утром, что ей консервировать хочется.\r\n\r\nПесочное время бежит сквозь суставы.\r\nОстанься, время, минутой летней!\r\nНо в банке - компота чуть-чуть осталось\r\nНа розовой кромке ночи и света.', '../img/pinksun.jpg', '2017-07-26 00:53:04', NULL, NULL),
(38, 'Севастополь', 'sevastopol', 'Поступь наощупь, ночь и безумно\r\nХочется спрятать бисер подальше...\r\nГде-то огни, смеются и шум\r\nРуку, скорее, и прочь, мой мальчик.\r\n\r\nЭти там-тамы - уже серьезно,\r\nЗначит война на соседнем поле.\r\nЭто по сути уже не звезды -\r\nЭто же души, не видишь чтоли?\r\n\r\nГород подталкивал улицы под ноги\r\nТо-то я все спотыкалась и падала\r\nСмешно при том кувыркаясь в воздухе\r\nА ты каждый раз желанье загадывал.\r\n\r\nОгней дополнения и обстоятельства\r\nЧертят залива тревожную линию\r\nИ может быть хватит словометательства\r\nСловами не выиграть битву длинную.\r\n\r\nА ты, на войне ведь! Мальчишкам весело...\r\nНебрежно спешился, выпил игристого\r\n(И так хорош - на стенку б повесила)\r\nКивнул и замер на миг от выстрела.', '../img/sevastopol.jpg', '2017-07-26 00:54:05', NULL, NULL),
(39, '1937', '1937', 'На тысячи кусочков\r\nРассыпалась ночь,\r\nКак звезды по небу и брань по кухне.\r\n\r\nИ двор холодел\r\nВ ожидании завтра,\r\nКак ждали страхи что свет потухнет.\r\n\r\nИ скрипнуло тихо\r\nС заднего хода.\r\nПарадная дверь - к заключительным проводам.\r\n\r\nА то - лишь мгновенье\r\nРазлуки повисло,\r\nЧтоб сердце сжалось как будто без повода.\r\n\r\nШаги засыхали,\r\nКак выстрелы свежие.\r\nВ тот год так много пропало без вести.\r\n\r\nВот двор,\r\nИ черная рама оконная,\r\nИ сотни причин беспробудной трезвости.', '../img/1937.jpg', '2017-07-26 00:55:08', NULL, NULL),
(40, 'Зверинец', 'wildbeastshew', 'Мне странно чувствовать, что я уже не я -\r\nЧто нет ни трещины, ни ямки, ни изъяна.\r\nИ изгнана пустая обезьяна,\r\nЧто так всегда боялась ноября.\r\n\r\nЯ даже не боюсь о том сказать\r\nИ, вроде, даже думаю иначе,\r\nИ, верно, подберет меня теперь собачник,\r\nС которым я как добрый пес смогу играть...\r\n\r\nЯ, вероятно, даже больше не свинья -\r\nСвой трюфель найден, жадность миновала,\r\nИ страх свой так глубОко закопала,\r\nЧто не отыщет туча воронья.\r\n\r\nЯ - лев, который вырос наконец\r\nИ отражение в реке весьма солидно.\r\nМеня, пожалуй, фразою обидной\r\nНе поразит уж ни один наглец.\r\n\r\nДовольно басен - спето их сполна.\r\nДовольно слов - я выбрался из смысла.\r\nИ выгнусь я упрямым коромыслом,\r\nКогда накроет всех одна волна.\r\n\r\nНе знаю лишь как из других изгнать\r\nЖестокую, тупую обезьяну,\r\nЧтоб свиньи не казались львами спьяну,\r\nА псы так не подлизывали знать.\r\n\r\nВы скажете - давай вперед иди,\r\nВеди других, раз знаешь слишком много.\r\nНо не люблю я пыльную дорогу -\r\nА потому не вырву сердце из груди.\r\n\r\nСвинья всегда останется свиньей,\r\nИ преданность собаки мер не знает,\r\nА обезьяна как всегда вас закидает -\r\nИ хорошо если древесною корой.\r\n\r\nУ львов своя сиеста - не до нас.\r\nВсем сытно, мягко, даже интересно,\r\nИ пол зверинца, развалившись в креслах,\r\nУ телевизоров уснет в урочный час.\r\n\r\nА я пойду, стряхнув свою печаль,\r\nБродить по снам или дорожкам лунным\r\nЯ не кажусь себе особо умным,\r\nИ потому мне быть собой не жаль.', '../img/wildbeastshew.jpg', '2017-07-26 00:56:39', NULL, NULL),
(41, 'Моряк', 'sailor', 'Я чувствую рифмы у горла, как волны с известной картины,\r\nМы с ними знакомы так долго. Бок о бок с моей бригантиной\r\nОни подрастали и пенились: барашки смешные сначала,\r\nПотом путеводные волны, а вот как теперь раскачало.\r\n\r\nНо это еще не девятый, а лишь в отчетный декабрь\r\nПосейдон кулаком потрясает, велит подлатать корабль. -\r\nДавай посвежее рабов на весла сажай и снова\r\nОт сердца до новых портов ищи подходящее слово.\r\n\r\nЯ был ли когда капитаном - матроски и той ведь нету\r\nИ вряд ли когда я стану - не тот, чтоб поплыть по свету.\r\nЯ как попугай пирата - могу повторить, что хочешь,\r\nДаже если когда-то ты судну тайфун напророчишь\r\n\r\nЯ славный, я ловкий, сметливый, тебе хоть немного нравлюсь?\r\nТряхнешь на воде смотри - я мыслями сразу заправлюсь\r\nПридумаю тысячи кладов, отрою такие вещи,\r\nЧтоб ты мне шепнул - "ну ладно, плыви еще, человече"...', '../img/sailor.jpg', '2017-07-26 00:57:45', NULL, NULL),
(42, 'Два дурака', 'twofools', 'Бедняга эфенди нередко совсем один,\r\nХодит, как тень, на поясе - ятаган.\r\nВсё удивляется: как дожить до седин?\r\n(Вроде молод ещё, а ни юн, ни пьян).\r\n\r\nВот только пару слов бросил - "могу подождать",\r\nА сколько камней он на душу положил.\r\nХуже, пожалуй, лишь на плахе лежать,\r\nКогда к тебе шествует мастер крюков и жил.\r\n\r\nИ кто же та дева, что стала тому виной?\r\nСовсем из других краёв, а той же бедой больна:\r\nОна одного подождёт, эфенди - её...\r\nВот уже тысячу лет ни покоя, ни сна.\r\n\r\nИ был бы кто на примете, кто на язык мастак:\r\nОн прямо бы в уши этим двоим сказал:\r\n"Вряд ли ребята протянете вы до ста,\r\nЕсли поверите чести, а не сердцам".\r\n\r\nНочь, соловей и стандартный розовый куст,\r\n(Можете заменить на канаву отхожих мест),\r\nВот бы хоть тут сорвались слова бы с уст\r\nС верой, что кто-то не выдаст, а кто-то не съест.\r\n\r\nДумала, будет он - благородный дон:\r\nЗдраво, вроде бы, судит, не рубит с плеч,\r\nНу так и с ним по всему не построишь дом,\r\nСына не вырастишь, коли в рассудке течь.\r\n\r\nЗнать, для тебя, эфенди, я - та же дыра,\r\nЯ и сама в такую сейчас гляжу...\r\nТолько лишь это и получается на ура,\r\nА ты как дурак твердишь: "Ничего, подожду"...\r\n\r\nЛучше, как встарь: в сердце - яд, в рот - кинжал\r\n(Я позабыла: как-то наоборот?)\r\nТак, может, р-раз – и все, никого не жаль?\r\nДуши в Геенне Огненной, кровь напоила рот.\r\n\r\nМы же с тобой не мелочны: две души,\r\nСлепим себя, не смея признаться в том,\r\nЧто лучше неблагородно мы поспешим,\r\nА кто-то нас подождёт с пересохшим ртом...', '../img/twofools.jpg', '2017-07-26 01:00:17', NULL, NULL),
(43, 'Исповедь поэта', 'poetsconfession', 'Мне сегодня, Мартын, сказали:\r\n"Если гений - забудь о лени,\r\nНе откладывай в долгий ящик\r\nСвои мысли для поколений.\r\n\r\nВедь у гения век недолог,\r\nПотому ты пиши почаще,\r\nЧтоб оставить всем тем, кто дорог,\r\nПламень спящий".\r\n\r\nМне сегодня, Мартын, сказали:\r\n"Если гений - забудь о славе,\r\nНе бахвалься своим юродством,\r\nСловно золото ты во сплаве,\r\n\r\nКаждый шаг, что прожил, страдая,\r\nПроживать умей с благородством.\r\nВедь в душе твоей ангел тайно\r\nС бесом бьется."\r\n\r\nМне сегодня, Мартын, сказали:\r\n"Если гений - забудь о счастье.\r\nЭти праздники, семьи, дети -\r\nНе видать тебе даже часть их.\r\n\r\nНе тебе бросать в землю семя,\r\nНо тебе быть за все в ответе,\r\nДля тебя семья - это племя,\r\nЖизни эти".\r\n\r\nМне сегодня, Мартын, сказали,\r\nКак отрезали, и я плачу.\r\nЯ узнал, что рожден слезами,\r\nА я думал - рожден удачей.\r\n\r\nНо я просто один, кто многих,\r\nЛишь на пару ударов сердца,\r\nНа обычной земной дороге\r\nОбогнал, чтоб в ночи согреться.\r\n', '../img/poetsconfession.jpg', '2017-07-26 01:01:43', NULL, NULL),
(44, 'Письмо мистеру О', 'orwellletter', 'Дожди в Океании поутру,\r\nТуман и сплошные ямы.\r\nСигнал к отступлению "ту-ру-ру"\r\nИграет рожок упрямый.\r\n\r\nНа мысли, должно быть, я так богат,\r\nВ проблемы одет со вкусом,\r\nПлыву по волнам я, и твой фрегат\r\nИдет параллельным курсом.\r\n\r\nСебя променяли на клеть и плеть:\r\nКого-то это заводит.\r\nИ выбор: "Иметь или не иметь"\r\nНе к каждому в жизни приходит.\r\n\r\nЯ гордо поправлю твой злой оскал:\r\nМарать кулаки о лица\r\nУже не впервой - как же я устал\r\nОт всех мировых полиций.\r\n\r\nХотел потеряться и нет меня\r\nВ растворе земных полюций.\r\nВернулся обратно к истокам дня,\r\nГде мысли о время бьются.\r\n\r\nНа мире включен обратный отсчет:\r\nЗемля на дары богата,\r\nА значит, работа во всю идет\r\nВ чертогах Большого Брата.\r\n\r\nЯ винтик системы, агнец, алтарь.\r\nНе Уинстон, не Смит, не Черчилль.\r\nИ Бог из машины - мой государь.\r\nНа карте мой путь расчерчен.', '../img/orwellletter.jpg', '2017-07-26 01:02:40', NULL, NULL);
INSERT INTO `poetry` (`id`, `title`, `name`, `source`, `img`, `date`, `rating`, `shares`) VALUES
(45, 'Пинкман', 'pinkman', 'Ты до жил докопаешься, сразу язык развяжешь.\r\nТолько что ты резину тянешь, уже не тащишь.\r\nОторвешься как лист по осени от осины\r\nТам буран за Стеной занесет, взгляд твой станет синим.\r\n\r\nОтвернулся, ушел, не трепи, что это проверка .\r\nТолько я без тебя как Пинкман без Хайзенберга.\r\nЯ как матерь драконов без самого злого - черного.\r\nНикому ни сказать, ни соврать - время чертово.\r\n\r\nНет нормально, работаем, как всегда над кармой:\r\nЯ сама себе Клара, Карл, и сама себе Карл я.\r\nТолько пару ухмылок смахну, после третьего шота:\r\nГде-то колокол, бьют в набат. Суббота.', '../img/pinkman.jpg', '2017-07-26 01:03:41', NULL, NULL),
(46, 'Сорбет и Вселенная', 'sorbetanduniverse', 'По тарелке размажу я свой сорбет,\r\nКак на завтрак овсянку в детстве.\r\nТы дождешься - однажды сойдут на нет,\r\nВсе мечты о твоем соседстве.\r\n\r\nКак погода в Дувре, мой старый френд?\r\nНе с Атлантики ль ветры дуют\r\nХочешь миром править - лови момент со словами:\r\nCome on, just do it.\r\n\r\nТы же за руку просто меня веди,\r\nТы умеешь, я знаю точно.\r\nВ Океании - слышал? - опять дожди\r\nЕжедневно и еженощно.\r\n\r\nЯ доем десерт - этот сладкий лед\r\nЗубы сводит мне неотвратно\r\nТак наш мир до точки себя сожмет,\r\nА потом развернет обратно.', '../img/sorbetanduniverse.jpg', '2017-07-26 01:04:40', NULL, NULL);
